import React from 'react';
import { motion } from 'framer-motion';
import { useLanguage } from '../components/Layout';
import { content } from '../lib/data';
import { Target, Eye, Heart, Award, Users, Globe, Sparkles, Mountain, Compass } from 'lucide-react';
import SEO from '../components/SEO';
import HeroCarousel from '../components/HeroCarousel';
import OptimizedImage from '../components/OptimizedImage';

const About: React.FC = () => {
    const { language } = useLanguage();
    const t = content[language].aboutFull;
    const nav = content[language].nav;

    const heroImages = [
        '/images/hero_background_1764603109548.webp',
        '/images/shibam_hadramout_1764603231216.webp',
        '/images/socotra_dragon_tree_1764603178352.webp'
    ];

    const timeline = [
        {
            year: '2020',
            title: { ar: 'البداية', en: 'The Beginning' },
            description: { ar: 'بدأت رحلتنا بحلم بسيط: مشاركة جمال اليمن مع العالم', en: 'Our journey began with a simple dream: sharing Yemen\'s beauty with the world' },
            icon: Sparkles
        },
        {
            year: '2021',
            title: { ar: 'النمو', en: 'Growth' },
            description: { ar: 'توسعنا لنشمل 10+ وجهات سياحية فريدة', en: 'Expanded to include 10+ unique destinations' },
            icon: Mountain
        },
        {
            year: '2023',
            title: { ar: 'الابتكار', en: 'Innovation' },
            description: { ar: 'أطلقنا تجارب سياحية مخصصة ومستدامة', en: 'Launched personalized and sustainable tourism experiences' },
            icon: Compass
        }
    ];

    const values = [
        {
            icon: Award,
            title: { ar: 'الجودة', en: 'Quality' },
            description: { ar: 'نلتزم بتقديم أعلى مستويات الجودة في كل رحلة', en: 'We commit to delivering the highest quality in every journey' }
        },
        {
            icon: Users,
            title: { ar: 'الضيافة', en: 'Hospitality' },
            description: { ar: 'نرحب بكم بكرم الضيافة اليمنية الأصيلة', en: 'We welcome you with authentic Yemeni hospitality' }
        },
        {
            icon: Globe,
            title: { ar: 'الاستدامة', en: 'Sustainability' },
            description: { ar: 'نحافظ على تراثنا الطبيعي والثقافي للأجيال القادمة', en: 'We preserve our natural and cultural heritage for future generations' }
        }
    ];

    return (
        <div className="bg-neutral-light dark:bg-neutral-900 transition-colors duration-300 min-h-screen">
            <SEO
                title={language === 'ar' ? 'من نحن' : 'About Us'}
                description={language === 'ar' ? 'تعرف على قصتنا ورؤيتنا.' : 'Learn about our story and vision.'}
            />

            {/* Hero Carousel */}
            <HeroCarousel
                images={heroImages}
                title={nav.about}
                subtitle={language === 'ar' ? 'اكتشف قصتنا ورؤيتنا' : 'Discover our story and vision'}
            />

            <div className="container mx-auto px-4 py-12 md:py-20 max-w-6xl">

                {/* Why Yemen Section */}
                <motion.div
                    initial={{ opacity: 0, y: 30 }}
                    whileInView={{ opacity: 1, y: 0 }}
                    viewport={{ once: true }}
                    className="mb-20"
                >
                    <div className="grid md:grid-cols-2 gap-12 items-center">
                        <div>
                            <h2 className="text-3xl md:text-5xl font-bold text-neutral-dark dark:text-white mb-6">
                                {language === 'ar' ? 'لماذا اليمن؟' : 'Why Yemen?'}
                            </h2>
                            <p className="text-lg text-neutral-dark/70 dark:text-gray-300 leading-relaxed mb-4">
                                {language === 'ar'
                                    ? 'اليمن، أرض السعادة، موطن لأقدم الحضارات وأجمل المناظر الطبيعية. من جبال الحجر الشاهقة إلى شواطئ سقطرى الساحرة، كل زاوية تحكي قصة عمرها آلاف السنين.'
                                    : 'Yemen, the land of happiness, home to ancient civilizations and stunning landscapes. From the towering Haraz Mountains to the enchanting Socotra shores, every corner tells a millennia-old story.'}
                            </p>
                            <p className="text-lg text-neutral-dark/70 dark:text-gray-300 leading-relaxed">
                                {language === 'ar'
                                    ? 'نحن هنا لجعل هذه التجربة ممكنة - آمنة، أصيلة، ولا تُنسى.'
                                    : 'We\'re here to make this experience possible - safe, authentic, and unforgettable.'}
                            </p>
                        </div>
                        <motion.div
                            initial={{ opacity: 0, scale: 0.95 }}
                            whileInView={{ opacity: 1, scale: 1 }}
                            viewport={{ once: true }}
                            className="relative h-[400px] rounded-3xl overflow-hidden shadow-xl"
                        >
                            <OptimizedImage
                                src="/images/socotra_dragon_tree_1764603178352.webp"
                                alt="Socotra Island"
                                className="w-full h-full object-cover"
                            />
                            <div className="absolute inset-0 bg-gradient-to-t from-black/40 to-transparent" />
                        </motion.div>
                    </div>
                </motion.div>

                {/* Timeline Section */}
                <motion.div
                    initial={{ opacity: 0, y: 30 }}
                    whileInView={{ opacity: 1, y: 0 }}
                    viewport={{ once: true }}
                    className="mb-20"
                >
                    <h2 className="text-3xl md:text-5xl font-bold text-neutral-dark dark:text-white mb-12 text-center">
                        {language === 'ar' ? 'رحلتنا' : 'Our Journey'}
                    </h2>

                    <div className="relative">
                        {/* Timeline Line */}
                        <div className="absolute left-1/2 transform -translate-x-1/2 h-full w-1 bg-primary/20 hidden md:block" />

                        {timeline.map((item, index) => (
                            <motion.div
                                key={index}
                                initial={{ opacity: 0, x: index % 2 === 0 ? -50 : 50 }}
                                whileInView={{ opacity: 1, x: 0 }}
                                viewport={{ once: true }}
                                transition={{ delay: index * 0.2 }}
                                className={`relative mb-12 md:mb-16 ${index % 2 === 0 ? 'md:pr-1/2 md:text-right' : 'md:pl-1/2 md:text-left'}`}
                            >
                                <div className={`md:w-1/2 ${index % 2 === 0 ? 'md:ml-auto md:pr-12' : 'md:pl-12'}`}>
                                    <div className="bg-white dark:bg-neutral-800 p-6 md:p-8 rounded-2xl shadow-lg hover:shadow-xl transition-shadow">
                                        {/* Year Badge */}
                                        <div className="absolute left-1/2 transform -translate-x-1/2 -translate-y-1/2 top-0 md:top-1/2 hidden md:flex items-center justify-center w-16 h-16 bg-primary text-white rounded-full font-bold shadow-lg">
                                            {item.year}
                                        </div>

                                        <div className="flex items-center gap-3 mb-4 md:hidden">
                                            <div className="w-12 h-12 bg-primary/10 rounded-full flex items-center justify-center">
                                                <item.icon className="w-6 h-6 text-primary" />
                                            </div>
                                            <span className="text-2xl font-bold text-primary">{item.year}</span>
                                        </div>

                                        <h3 className="text-xl md:text-2xl font-bold text-neutral-dark dark:text-white mb-3">
                                            {item.title[language]}
                                        </h3>
                                        <p className="text-neutral-dark/70 dark:text-gray-300 leading-relaxed">
                                            {item.description[language]}
                                        </p>
                                    </div>
                                </div>
                            </motion.div>
                        ))}
                    </div>
                </motion.div>

                {/* Mission & Vision - Side by Side */}
                <div className="grid md:grid-cols-2 gap-8 mb-20">
                    <motion.div
                        initial={{ opacity: 0, y: 30 }}
                        whileInView={{ opacity: 1, y: 0 }}
                        viewport={{ once: true }}
                        className="bg-gradient-to-br from-primary/5 to-primary/10 dark:from-primary/10 dark:to-primary/20 p-8 md:p-10 rounded-3xl"
                    >
                        <Target size={48} className="text-primary mb-6" />
                        <h2 className="text-2xl md:text-3xl font-bold text-neutral-dark dark:text-white mb-4">
                            {t.mission.title}
                        </h2>
                        <p className="text-lg text-neutral-dark/80 dark:text-gray-300 leading-relaxed">
                            {t.mission.description}
                        </p>
                    </motion.div>

                    <motion.div
                        initial={{ opacity: 0, y: 30 }}
                        whileInView={{ opacity: 1, y: 0 }}
                        viewport={{ once: true }}
                        transition={{ delay: 0.1 }}
                        className="bg-gradient-to-br from-secondary/5 to-secondary/10 dark:from-secondary/10 dark:to-secondary/20 p-8 md:p-10 rounded-3xl"
                    >
                        <Eye size={48} className="text-secondary mb-6" />
                        <h2 className="text-2xl md:text-3xl font-bold text-neutral-dark dark:text-white mb-4">
                            {t.vision.title}
                        </h2>
                        <p className="text-lg text-neutral-dark/80 dark:text-gray-300 leading-relaxed">
                            {t.vision.description}
                        </p>
                    </motion.div>
                </div>

                {/* Values Section */}
                <motion.div
                    initial={{ opacity: 0, y: 30 }}
                    whileInView={{ opacity: 1, y: 0 }}
                    viewport={{ once: true }}
                >
                    <div className="flex items-center justify-center gap-4 mb-12">
                        <Heart size={40} className="text-accent" />
                        <h2 className="text-3xl md:text-5xl font-bold text-neutral-dark dark:text-white">
                            {t.values.title}
                        </h2>
                    </div>
                    <div className="grid grid-cols-1 md:grid-cols-3 gap-6 md:gap-8">
                        {values.map((value, index) => (
                            <motion.div
                                key={index}
                                initial={{ opacity: 0, y: 30 }}
                                whileInView={{ opacity: 1, y: 0 }}
                                viewport={{ once: true }}
                                transition={{ delay: index * 0.1 }}
                                className="bg-white dark:bg-neutral-800 p-6 md:p-8 rounded-2xl shadow-lg hover:shadow-xl transition-all hover:-translate-y-2 border border-transparent dark:border-neutral-700"
                            >
                                <div className="bg-primary/10 w-14 h-14 md:w-16 md:h-16 rounded-full flex items-center justify-center mb-6 mx-auto">
                                    <value.icon size={28} className="text-primary" />
                                </div>
                                <h3 className="text-lg md:text-xl font-bold text-neutral-dark dark:text-white mb-3 md:mb-4 text-center">
                                    {value.title[language]}
                                </h3>
                                <p className="text-neutral-dark/70 dark:text-gray-300 text-center leading-relaxed text-sm md:text-base">
                                    {value.description[language]}
                                </p>
                            </motion.div>
                        ))}
                    </div>
                </motion.div>
            </div>
        </div>
    );
};

export default About;
