import React from 'react';
import { motion } from 'framer-motion';
import { useLanguage } from '../components/Layout';

const GalleryPage: React.FC = () => {
    const { language } = useLanguage();

    return (
        <div className="pt-24 pb-20 container mx-auto px-4 min-h-screen">
            <motion.div
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
            >
                <h1 className="text-4xl md:text-6xl font-bold text-secondary mb-12 text-center">
                    {language === 'ar' ? 'معرض الصور' : 'Photo Gallery'}
                </h1>

                <div className="text-center text-neutral-dark/60">
                    {/* Placeholder for the advanced carousel component we will build */}
                    <p>{language === 'ar' ? 'جاري تحميل المعرض...' : 'Loading Gallery...'}</p>
                </div>
            </motion.div>
        </div>
    );
};

export default GalleryPage;
