import React from 'react';
import { motion } from 'framer-motion';
import { useLanguage } from '../components/Layout';
import { content } from '../lib/data';
import { Shield, Heart, Leaf, ArrowRight } from 'lucide-react';
import { Link } from 'react-router-dom';

const About: React.FC = () => {
    const { language, dir } = useLanguage();
    const t = content[language].aboutFull;

    const icons = [Heart, Shield, Leaf];

    return (
        <div className="min-h-screen bg-neutral-light">
            {/* Hero Banner */}
            <div className="relative h-[40vh] bg-neutral-dark flex items-center justify-center overflow-hidden">
                <img
                    src="/images/almukalla_harbor_1764603566692.png"
                    alt="About Us Hero"
                    className="absolute inset-0 w-full h-full object-cover opacity-40"
                />
                <div className="relative z-10 text-center px-4">
                    <motion.h1
                        initial={{ opacity: 0, y: 20 }}
                        animate={{ opacity: 1, y: 0 }}
                        className="text-4xl md:text-6xl font-bold text-white mb-4"
                    >
                        {language === 'ar' ? 'قصتنا' : 'Our Story'}
                    </motion.h1>
                    <motion.div
                        initial={{ width: 0 }}
                        animate={{ width: '100px' }}
                        className="h-1 bg-primary mx-auto"
                    />
                </div>
            </div>

            <div className="container mx-auto px-4 py-20">
                {/* Mission & Vision */}
                <div className="grid grid-cols-1 md:grid-cols-2 gap-12 mb-20">
                    <motion.div
                        initial={{ opacity: 0, x: dir === 'rtl' ? 50 : -50 }}
                        whileInView={{ opacity: 1, x: 0 }}
                        viewport={{ once: true }}
                        className="bg-white p-8 rounded-2xl shadow-lg border-t-4 border-primary"
                    >
                        <h2 className="text-2xl font-bold text-neutral-dark mb-4">{t.mission.title}</h2>
                        <p className="text-lg text-neutral-dark/70 leading-relaxed">
                            {t.mission.description}
                        </p>
                    </motion.div>

                    <motion.div
                        initial={{ opacity: 0, x: dir === 'rtl' ? -50 : 50 }}
                        whileInView={{ opacity: 1, x: 0 }}
                        viewport={{ once: true }}
                        transition={{ delay: 0.2 }}
                        className="bg-white p-8 rounded-2xl shadow-lg border-t-4 border-secondary"
                    >
                        <h2 className="text-2xl font-bold text-neutral-dark mb-4">{t.vision.title}</h2>
                        <p className="text-lg text-neutral-dark/70 leading-relaxed">
                            {t.vision.description}
                        </p>
                    </motion.div>
                </div>

                {/* Values Section */}
                <div className="mb-20">
                    <div className="text-center mb-12">
                        <h2 className="text-3xl md:text-4xl font-bold text-neutral-dark mb-4">{t.values.title}</h2>
                        <div className="w-20 h-1 bg-secondary mx-auto rounded-full" />
                    </div>

                    <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
                        {t.values.items.map((item, index) => {
                            const Icon = icons[index % icons.length];
                            return (
                                <motion.div
                                    key={index}
                                    initial={{ opacity: 0, y: 20 }}
                                    whileInView={{ opacity: 1, y: 0 }}
                                    viewport={{ once: true }}
                                    transition={{ delay: index * 0.1 }}
                                    className="bg-white p-8 rounded-2xl shadow-sm hover:shadow-xl transition-shadow text-center group"
                                >
                                    <div className="w-16 h-16 bg-neutral-light rounded-full flex items-center justify-center mx-auto mb-6 group-hover:bg-primary/10 transition-colors">
                                        <Icon size={32} className="text-neutral-dark group-hover:text-primary transition-colors" />
                                    </div>
                                    <h3 className="text-xl font-bold text-neutral-dark mb-3">{item.title}</h3>
                                    <p className="text-neutral-dark/70">
                                        {item.desc}
                                    </p>
                                </motion.div>
                            );
                        })}
                    </div>
                </div>

                {/* CTA Section */}
                <motion.div
                    initial={{ opacity: 0, scale: 0.95 }}
                    whileInView={{ opacity: 1, scale: 1 }}
                    viewport={{ once: true }}
                    className="bg-gradient-to-r from-neutral-dark to-neutral-dark/90 rounded-3xl p-12 text-center text-white relative overflow-hidden"
                >
                    <div className="relative z-10">
                        <h2 className="text-3xl md:text-5xl font-bold mb-6">
                            {language === 'ar' ? 'جاهز لاستكشاف اليمن؟' : 'Ready to Explore Yemen?'}
                        </h2>
                        <p className="text-xl text-white/80 mb-8 max-w-2xl mx-auto">
                            {language === 'ar'
                                ? 'دعنا نساعدك في التخطيط لرحلة العمر. تواصل معنا اليوم!'
                                : 'Let us help you plan the trip of a lifetime. Contact us today!'}
                        </p>
                        <Link
                            to="/#contact"
                            className="inline-flex items-center gap-2 bg-primary hover:bg-primary/90 text-white px-8 py-4 rounded-full font-bold text-lg transition-transform hover:scale-105 shadow-lg"
                        >
                            {language === 'ar' ? 'تواصل معنا' : 'Contact Us'}
                            <ArrowRight className={dir === 'rtl' ? 'rotate-180' : ''} />
                        </Link>
                    </div>

                    {/* Decorative Circles */}
                    <div className="absolute top-0 left-0 w-64 h-64 bg-white/5 rounded-full blur-3xl -translate-x-1/2 -translate-y-1/2" />
                    <div className="absolute bottom-0 right-0 w-64 h-64 bg-primary/20 rounded-full blur-3xl translate-x-1/2 translate-y-1/2" />
                </motion.div>
            </div>
        </div>
    );
};

export default About;
