import React, { useState, useEffect } from 'react';
import { motion, useInView } from 'framer-motion';
import { useLanguage } from '../components/Layout';
import { content } from '../lib/data';
import { Target, Eye, Heart, Award, Users, Globe, TrendingUp, Shield, Zap } from 'lucide-react';
import SEO from '../components/SEO';
import HeroCarousel from '../components/HeroCarousel';
import OptimizedImage from '../components/OptimizedImage';

const AnimatedCounter: React.FC<{ end: number; duration?: number; suffix?: string }> = ({ end, duration = 2, suffix = '' }) => {
    const [count, setCount] = useState(0);
    const ref = React.useRef(null);
    const isInView = useInView(ref, { once: true });

    useEffect(() => {
        if (!isInView) return;

        let startTime: number;
        let animationFrame: number;

        const animate = (timestamp: number) => {
            if (!startTime) startTime = timestamp;
            const progress = Math.min((timestamp - startTime) / (duration * 1000), 1);

            setCount(Math.floor(progress * end));

            if (progress < 1) {
                animationFrame = requestAnimationFrame(animate);
            }
        };

        animationFrame = requestAnimationFrame(animate);
        return () => cancelAnimationFrame(animationFrame);
    }, [isInView, end, duration]);

    return <span ref={ref}>{count}{suffix}</span>;
};

const About: React.FC = () => {
    const { language } = useLanguage();
    const t = content[language].aboutFull;
    const nav = content[language].nav;

    const heroImages = [
        '/images/hero_background_1764603109548.webp',
        '/images/shibam_hadramout_1764603231216.webp',
        '/images/socotra_dragon_tree_1764603178352.webp'
    ];

    const stats = [
        { value: 5000, suffix: '+', label: language === 'ar' ? 'زائر سعيد' : 'Happy Visitors', icon: Users },
        { value: 20, suffix: '+', label: language === 'ar' ? 'وجهة سياحية' : 'Destinations', icon: Globe },
        { value: 99, suffix: '%', label: language === 'ar' ? 'رضا العملاء' : 'Satisfaction', icon: TrendingUp }
    ];

    const features = [
        {
            icon: Shield,
            title: { ar: 'السلامة أولاً', en: 'Safety First' },
            description: { ar: 'نضمن سلامتك في كل خطوة', en: 'We guarantee your safety at every step' }
        },
        {
            icon: Award,
            title: { ar: 'تجارب فريدة', en: 'Unique Experiences' },
            description: { ar: 'رحلات مخصصة تناسب اهتماماتك', en: 'Customized trips to match your interests' }
        },
        {
            icon: Zap,
            title: { ar: 'دعم 24/7', en: '24/7 Support' },
            description: { ar: 'فريقنا متاح دائماً لمساعدتك', en: 'Our team is always available to help' }
        }
    ];

    const values = [
        {
            icon: Award,
            title: { ar: 'الجودة', en: 'Quality' },
            description: { ar: 'نلتزم بتقديم أعلى مستويات الجودة في كل رحلة', en: 'We commit to delivering the highest quality in every journey' }
        },
        {
            icon: Users,
            title: { ar: 'الضيافة', en: 'Hospitality' },
            description: { ar: 'نرحب بكم بكرم الضيافة اليمنية الأصيلة', en: 'We welcome you with authentic Yemeni hospitality' }
        },
        {
            icon: Globe,
            title: { ar: 'الاستدامة', en: 'Sustainability' },
            description: { ar: 'نحافظ على تراثنا الطبيعي والثقافي للأجيال القادمة', en: 'We preserve our natural and cultural heritage for future generations' }
        }
    ];

    return (
        <div className="bg-neutral-light dark:bg-neutral-900 transition-colors duration-300 min-h-screen">
            <SEO
                title={language === 'ar' ? 'من نحن' : 'About Us'}
                description={language === 'ar' ? 'تعرف على قصتنا ورؤيتنا.' : 'Learn about our story and vision.'}
            />

            <HeroCarousel
                images={heroImages}
                title={nav.about}
                subtitle={language === 'ar' ? 'اكتشف قصتنا ورؤيتنا' : 'Discover our story and vision'}
            />

            <div className="container mx-auto px-4 py-12 md:py-20 max-w-6xl">

                {/* Stats Section with Animated Counters */}
                <motion.div
                    initial={{ opacity: 0, y: 30 }}
                    whileInView={{ opacity: 1, y: 0 }}
                    viewport={{ once: true }}
                    className="mb-20"
                >
                    <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                        {stats.map((stat, index) => (
                            <motion.div
                                key={index}
                                initial={{ opacity: 0, scale: 0.8 }}
                                whileInView={{ opacity: 1, scale: 1 }}
                                viewport={{ once: true }}
                                transition={{ delay: index * 0.2 }}
                                className="relative bg-gradient-to-br from-primary/10 to-primary/5 dark:from-primary/20 dark:to-primary/10 p-8 rounded-3xl text-center group hover:shadow-xl transition-all hover:-translate-y-2 overflow-hidden"
                            >
                                <div className="absolute top-0 right-0 w-32 h-32 bg-primary/5 rounded-full blur-3xl -translate-y-1/2 translate-x-1/2 group-hover:scale-150 transition-transform" />
                                <stat.icon className="w-12 h-12 text-primary mx-auto mb-4" />
                                <div className="text-4xl md:text-5xl font-bold text-neutral-dark dark:text-white mb-2">
                                    <AnimatedCounter end={stat.value} suffix={stat.suffix} />
                                </div>
                                <p className="text-neutral-dark/60 dark:text-gray-400 font-medium">{stat.label}</p>
                            </motion.div>
                        ))}
                    </div>
                </motion.div>

                {/* Features Grid */}
                <motion.div
                    initial={{ opacity: 0, y: 30 }}
                    whileInView={{ opacity: 1, y: 0 }}
                    viewport={{ once: true }}
                    className="mb-20"
                >
                    <h2 className="text-3xl md:text-4xl font-bold text-neutral-dark dark:text-white mb-12 text-center">
                        {language === 'ar' ? 'لماذا تختارنا؟' : 'Why Choose Us?'}
                    </h2>
                    <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                        {features.map((feature, index) => (
                            <motion.div
                                key={index}
                                initial={{ opacity: 0, y: 30 }}
                                whileInView={{ opacity: 1, y: 0 }}
                                viewport={{ once: true }}
                                transition={{ delay: index * 0.15 }}
                                whileHover={{ scale: 1.05 }}
                                className="bg-white dark:bg-neutral-800 p-6 rounded-2xl shadow-md hover:shadow-xl transition-all cursor-pointer"
                            >
                                <div className="w-14 h-14 bg-primary/10 rounded-xl flex items-center justify-center mb-4">
                                    <feature.icon className="w-7 h-7 text-primary" />
                                </div>
                                <h3 className="text-xl font-bold text-neutral-dark dark:text-white mb-3">
                                    {feature.title[language]}
                                </h3>
                                <p className="text-neutral-dark/70 dark:text-gray-300 leading-relaxed">
                                    {feature.description[language]}
                                </p>
                            </motion.div>
                        ))}
                    </div>
                </motion.div>

                {/* Mission & Vision Cards */}
                <div className="grid md:grid-cols-2 gap-8 mb-20">
                    <motion.div
                        initial={{ opacity: 0, x: -50 }}
                        whileInView={{ opacity: 1, x: 0 }}
                        viewport={{ once: true }}
                        className="relative bg-gradient-to-br from-primary/10 via-primary/5 to-transparent dark:from-primary/20 dark:via-primary/10 p-8 md:p-10 rounded-3xl overflow-hidden group hover:shadow-2xl transition-all"
                    >
                        <div className="absolute -top-10 -right-10 w-40 h-40 bg-primary/10 rounded-full blur-3xl group-hover:scale-150 transition-transform" />
                        <Target size={56} className="text-primary mb-6 relative z-10" />
                        <h2 className="text-2xl md:text-3xl font-bold text-neutral-dark dark:text-white mb-4 relative z-10">
                            {t.mission.title}
                        </h2>
                        <p className="text-lg text-neutral-dark/80 dark:text-gray-300 leading-relaxed relative z-10">
                            {t.mission.description}
                        </p>
                    </motion.div>

                    <motion.div
                        initial={{ opacity: 0, x: 50 }}
                        whileInView={{ opacity: 1, x: 0 }}
                        viewport={{ once: true }}
                        transition={{ delay: 0.2 }}
                        className="relative bg-gradient-to-br from-secondary/10 via-secondary/5 to-transparent dark:from-secondary/20 dark:via-secondary/10 p-8 md:p-10 rounded-3xl overflow-hidden group hover:shadow-2xl transition-all"
                    >
                        <div className="absolute -top-10 -right-10 w-40 h-40 bg-secondary/10 rounded-full blur-3xl group-hover:scale-150 transition-transform" />
                        <Eye size={56} className="text-secondary mb-6 relative z-10" />
                        <h2 className="text-2xl md:text-3xl font-bold text-neutral-dark dark:text-white mb-4 relative z-10">
                            {t.vision.title}
                        </h2>
                        <p className="text-lg text-neutral-dark/80 dark:text-gray-300 leading-relaxed relative z-10">
                            {t.vision.description}
                        </p>
                    </motion.div>
                </div>

                {/* Values Section */}
                <motion.div
                    initial={{ opacity: 0, y: 30 }}
                    whileInView={{ opacity: 1, y: 0 }}
                    viewport={{ once: true }}
                >
                    <div className="flex items-center justify-center gap-4 mb-12">
                        <Heart size={40} className="text-accent" />
                        <h2 className="text-3xl md:text-4xl font-bold text-neutral-dark dark:text-white">
                            {t.values.title}
                        </h2>
                    </div>
                    <div className="grid grid-cols-1 md:grid-cols-3 gap-6 md:gap-8">
                        {values.map((value, index) => (
                            <motion.div
                                key={index}
                                initial={{ opacity: 0, y: 40 }}
                                whileInView={{ opacity: 1, y: 0 }}
                                viewport={{ once: true }}
                                transition={{ delay: index * 0.15 }}
                                whileHover={{ y: -8 }}
                                className="bg-white dark:bg-neutral-800 p-6 md:p-8 rounded-3xl shadow-lg hover:shadow-2xl transition-all border border-transparent dark:border-neutral-700 relative overflow-hidden group"
                            >
                                <div className="absolute top-0 right-0 w-24 h-24 bg-primary/5 rounded-full blur-2xl -translate-y-1/2 translate-x-1/2 group-hover:scale-150 transition-transform" />
                                <div className="bg-primary/10 w-16 h-16 rounded-full flex items-center justify-center mb-6 mx-auto relative z-10">
                                    <value.icon size={32} className="text-primary" />
                                </div>
                                <h3 className="text-xl font-bold text-neutral-dark dark:text-white mb-4 text-center relative z-10">
                                    {value.title[language]}
                                </h3>
                                <p className="text-neutral-dark/70 dark:text-gray-300 text-center leading-relaxed relative z-10">
                                    {value.description[language]}
                                </p>
                            </motion.div>
                        ))}
                    </div>
                </motion.div>
            </div>
        </div>
    );
};

export default About;
