import React, { useState, useEffect } from 'react';
import { motion, useInView } from 'framer-motion';
import { useLanguage } from '../components/Layout';
import { content } from '../lib/data';
import { Target, Eye, Heart, Award, Users, Globe, TrendingUp, Shield, Zap, ChevronRight } from 'lucide-react';
import SEO from '../components/SEO';
import OptimizedImage from '../components/OptimizedImage';

const AnimatedCounter: React.FC<{ end: number; duration?: number; suffix?: string }> = ({ end, duration = 2, suffix = '' }) => {
    const [count, setCount] = useState(0);
    const ref = React.useRef(null);
    const isInView = useInView(ref, { once: true });

    useEffect(() => {
        if (!isInView) return;
        let startTime: number;
        let animationFrame: number;
        const animate = (timestamp: number) => {
            if (!startTime) startTime = timestamp;
            const progress = Math.min((timestamp - startTime) / (duration * 1000), 1);
            setCount(Math.floor(progress * end));
            if (progress < 1) {
                animationFrame = requestAnimationFrame(animate);
            }
        };
        animationFrame = requestAnimationFrame(animate);
        return () => cancelAnimationFrame(animationFrame);
    }, [isInView, end, duration]);

    return <span ref={ref}>{count}{suffix}</span>;
};

const FlipCard: React.FC<{ front: React.ReactNode; back: React.ReactNode; index: number }> = ({ front, back, index }) => {
    const [isFlipped, setIsFlipped] = useState(false);

    return (
        <motion.div
            initial={{ opacity: 0, y: 50 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ delay: index * 0.2 }}
            className="relative h-[400px] md:h-[450px] perspective-1000 cursor-pointer"
            onClick={() => setIsFlipped(!isFlipped)}
        >
            <motion.div
                className="relative w-full h-full preserve-3d transition-all duration-700"
                animate={{ rotateY: isFlipped ? 180 : 0 }}
                style={{ transformStyle: 'preserve-3d' }}
            >
                {/* Front */}
                <div className="absolute inset-0 w-full h-full backface-hidden rounded-3xl overflow-hidden shadow-2xl">
                    {front}
                </div>
                {/* Back */}
                <div
                    className="absolute inset-0 w-full h-full backface-hidden rounded-3xl overflow-hidden shadow-2xl rotate-y-180"
                    style={{ backfaceVisibility: 'hidden', transform: 'rotateY(180deg)' }}
                >
                    {back}
                </div>
            </motion.div>
        </motion.div>
    );
};

const About: React.FC = () => {
    const { language } = useLanguage();
    const t = content[language].aboutFull;

    const stats = [
        { value: 5000, suffix: '+', label: language === 'ar' ? 'زائر سعيد' : 'Happy Visitors', icon: Users, color: 'from-blue-500 to-purple-600' },
        { value: 20, suffix: '+', label: language === 'ar' ? 'وجهة سياحية' : 'Destinations', icon: Globe, color: 'from-green-500 to-teal-600' },
        { value: 99, suffix: '%', label: language === 'ar' ? 'رضا العملاء' : 'Satisfaction', icon: TrendingUp, color: 'from-orange-500 to-red-600' },
        { value: 24, suffix: '/7', label: language === 'ar' ? 'دعم متواصل' : 'Support', icon: Shield, color: 'from-pink-500 to-rose-600' }
    ];

    const features = [
        {
            icon: Shield,
            title: { ar: 'السلامة أولاً', en: 'Safety First' },
            description: { ar: 'نضمن سلامتك في كل خطوة', en: 'We guarantee your safety at every step' },
            gradient: 'from-blue-500 to-cyan-500'
        },
        {
            icon: Award,
            title: { ar: 'تجارب فريدة', en: 'Unique Experiences' },
            description: { ar: 'رحلات مخصصة تناسب اهتماماتك', en: 'Customized trips to match your interests' },
            gradient: 'from-purple-500 to-pink-500'
        },
        {
            icon: Zap,
            title: { ar: 'دعم فوري', en: 'Instant Support' },
            description: { ar: 'فريقنا متاح دائماً لمساعدتك', en: 'Our team is always available to help' },
            gradient: 'from-orange-500 to-yellow-500'
        }
    ];

    const values = [
        { icon: Award, title: { ar: 'الجودة', en: 'Quality' }, description: { ar: 'نلتزم بتقديم أعلى مستويات الجودة في كل رحلة', en: 'We commit to delivering the highest quality in every journey' } },
        { icon: Users, title: { ar: 'الضيافة', en: 'Hospitality' }, description: { ar: 'نرحب بكم بكرم الضيافة اليمنية الأصيلة', en: 'We welcome you with authentic Yemeni hospitality' } },
        { icon: Globe, title: { ar: 'الاستدامة', en: 'Sustainability' }, description: { ar: 'نحافظ على تراثنا الطبيعي والثقافي للأجيال القادمة', en: 'We preserve our natural and cultural heritage for future generations' } }
    ];

    return (
        <div className="bg-neutral-light dark:bg-neutral-900 transition-colors duration-300 min-h-screen">
            <SEO
                title={language === 'ar' ? 'من نحن' : 'About Us'}
                description={language === 'ar' ? 'تعرف على قصتنا ورؤيتنا.' : 'Learn about our story and vision.'}
            />

            {/* Full-Screen Hero with Overlay Stats */}
            <div className="relative h-screen min-h-[600px] overflow-hidden">
                <OptimizedImage
                    src="/images/hero_background_1764603109548.webp"
                    alt="Yemen"
                    className="absolute inset-0 w-full h-full object-cover"
                />
                <div className="absolute inset-0 bg-gradient-to-b from-black/70 via-black/50 to-neutral-light dark:to-neutral-900" />

                <div className="relative z-10 container mx-auto px-4 h-full flex flex-col justify-center items-center text-center text-white">
                    <motion.div
                        initial={{ opacity: 0, y: 30 }}
                        animate={{ opacity: 1, y: 0 }}
                        transition={{ duration: 0.8 }}
                    >
                        <h1 className="text-4xl md:text-7xl font-bold mb-6">
                            {language === 'ar' ? 'من نحن' : 'About Us'}
                        </h1>
                        <p className="text-xl md:text-2xl text-white/90 max-w-2xl mx-auto mb-12">
                            {language === 'ar' ? 'اكتشف قصتنا ورؤيتنا' : 'Discover our story and vision'}
                        </p>
                    </motion.div>

                    {/* Horizontal Scroll Stats */}
                    <motion.div
                        initial={{ opacity: 0, y: 50 }}
                        animate={{ opacity: 1, y: 0 }}
                        transition={{ delay: 0.4, duration: 0.8 }}
                        className="w-full max-w-6xl"
                    >
                        <div className="flex gap-4 overflow-x-auto snap-x snap-mandatory pb-4 scrollbar-hide px-4 md:px-0">
                            {stats.map((stat, index) => (
                                <motion.div
                                    key={index}
                                    initial={{ opacity: 0, scale: 0.8 }}
                                    animate={{ opacity: 1, scale: 1 }}
                                    transition={{ delay: 0.6 + (index * 0.1) }}
                                    className="snap-center flex-shrink-0 w-[200px] md:w-[240px]"
                                >
                                    <div className={`bg-gradient-to-br ${stat.color} p-6 rounded-2xl text-white shadow-2xl`}>
                                        <stat.icon className="w-10 h-10 mb-3 opacity-80" />
                                        <div className="text-4xl font-bold mb-1">
                                            <AnimatedCounter end={stat.value} suffix={stat.suffix} />
                                        </div>
                                        <p className="text-sm opacity-90">{stat.label}</p>
                                    </div>
                                </motion.div>
                            ))}
                        </div>
                    </motion.div>
                </div>

                {/* Scroll Indicator */}
                <motion.div
                    initial={{ opacity: 0 }}
                    animate={{ opacity: 1 }}
                    transition={{ delay: 1.2 }}
                    className="absolute bottom-8 left-1/2 transform -translate-x-1/2 text-white"
                >
                    <motion.div
                        animate={{ y: [0, 10, 0] }}
                        transition={{ repeat: Infinity, duration: 1.5 }}
                    >
                        <ChevronRight className="rotate-90 w-8 h-8" />
                    </motion.div>
                </motion.div>
            </div>

            <div className="container mx-auto px-4 py-12 md:py-20 max-w-6xl">

                {/* Horizontal "Story" Features */}
                <div className="mb-20">
                    <h2 className="text-3xl md:text-4xl font-bold text-neutral-dark dark:text-white mb-8 text-center">
                        {language === 'ar' ? 'لماذا تختارنا؟' : 'Why Choose Us?'}
                    </h2>
                    <div className="flex gap-4 overflow-x-auto snap-x snap-mandatory pb-4 scrollbar-hide -mx-4 px-4">
                        {features.map((feature, index) => (
                            <motion.div
                                key={index}
                                initial={{ opacity: 0, x: 50 }}
                                whileInView={{ opacity: 1, x: 0 }}
                                viewport={{ once: true }}
                                transition={{ delay: index * 0.15 }}
                                className="snap-center flex-shrink-0 w-[280px] md:w-[320px]"
                            >
                                <div className={`relative bg-gradient-to-br ${feature.gradient} p-8 rounded-3xl text-white shadow-xl h-[300px] flex flex-col justify-between overflow-hidden group`}>
                                    <div className="absolute top-0 right-0 w-32 h-32 bg-white/10 rounded-full blur-3xl -translate-y-1/2 translate-x-1/2 group-hover:scale-150 transition-transform" />
                                    <feature.icon className="w-12 h-12 relative z-10" />
                                    <div className="relative z-10">
                                        <h3 className="text-2xl font-bold mb-3">{feature.title[language]}</h3>
                                        <p className="text-white/90">{feature.description[language]}</p>
                                    </div>
                                </div>
                            </motion.div>
                        ))}
                    </div>
                </div>

                {/* Flip Cards: Mission & Vision */}
                <div className="mb-20">
                    <h2 className="text-3xl md:text-4xl font-bold text-neutral-dark dark:text-white mb-4 text-center">
                        {language === 'ar' ? 'رسالتنا ورؤيتنا' : 'Our Mission & Vision'}
                    </h2>
                    <p className="text-center text-neutral-dark/60 dark:text-gray-400 mb-12">
                        {language === 'ar' ? 'اضغط للكشف' : 'Tap to reveal'}
                    </p>
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
                        <FlipCard
                            index={0}
                            front={
                                <div className="relative w-full h-full bg-gradient-to-br from-primary/90 to-primary p-10 flex flex-col justify-center items-center text-white">
                                    <Target size={64} className="mb-6" />
                                    <h3 className="text-3xl font-bold">{t.mission.title}</h3>
                                    <p className="text-sm mt-4 opacity-75">{language === 'ar' ? 'اضغط للمزيد' : 'Tap for more'}</p>
                                </div>
                            }
                            back={
                                <div className="w-full h-full bg-white dark:bg-neutral-800 p-10 flex flex-col justify-center">
                                    <Target size={48} className="text-primary mb-6" />
                                    <h3 className="text-2xl font-bold text-neutral-dark dark:text-white mb-4">{t.mission.title}</h3>
                                    <p className="text-neutral-dark/80 dark:text-gray-300 leading-relaxed">{t.mission.description}</p>
                                </div>
                            }
                        />
                        <FlipCard
                            index={1}
                            front={
                                <div className="relative w-full h-full bg-gradient-to-br from-secondary/90 to-secondary p-10 flex flex-col justify-center items-center text-white">
                                    <Eye size={64} className="mb-6" />
                                    <h3 className="text-3xl font-bold">{t.vision.title}</h3>
                                    <p className="text-sm mt-4 opacity-75">{language === 'ar' ? 'اضغط للمزيد' : 'Tap for more'}</p>
                                </div>
                            }
                            back={
                                <div className="w-full h-full bg-white dark:bg-neutral-800 p-10 flex flex-col justify-center">
                                    <Eye size={48} className="text-secondary mb-6" />
                                    <h3 className="text-2xl font-bold text-neutral-dark dark:text-white mb-4">{t.vision.title}</h3>
                                    <p className="text-neutral-dark/80 dark:text-gray-300 leading-relaxed">{t.vision.description}</p>
                                </div>
                            }
                        />
                    </div>
                </div>

                {/* Values Grid */}
                <motion.div
                    initial={{ opacity: 0, y: 30 }}
                    whileInView={{ opacity: 1, y: 0 }}
                    viewport={{ once: true }}
                >
                    <div className="flex items-center justify-center gap-4 mb-12">
                        <Heart size={40} className="text-accent" />
                        <h2 className="text-3xl md:text-4xl font-bold text-neutral-dark dark:text-white">
                            {t.values.title}
                        </h2>
                    </div>
                    <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                        {values.map((value, index) => (
                            <motion.div
                                key={index}
                                initial={{ opacity: 0, y: 40 }}
                                whileInView={{ opacity: 1, y: 0 }}
                                viewport={{ once: true }}
                                transition={{ delay: index * 0.15 }}
                                whileHover={{ y: -8, scale: 1.02 }}
                                className="bg-white dark:bg-neutral-800 p-6 md:p-8 rounded-3xl shadow-lg hover:shadow-2xl transition-all border border-neutral-200 dark:border-neutral-700"
                            >
                                <div className="bg-primary/10 w-16 h-16 rounded-full flex items-center justify-center mb-6 mx-auto">
                                    <value.icon size={32} className="text-primary" />
                                </div>
                                <h3 className="text-xl font-bold text-neutral-dark dark:text-white mb-4 text-center">
                                    {value.title[language]}
                                </h3>
                                <p className="text-neutral-dark/70 dark:text-gray-300 text-center leading-relaxed">
                                    {value.description[language]}
                                </p>
                            </motion.div>
                        ))}
                    </div>
                </motion.div>
            </div>
        </div>
    );
};

export default About;
