import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { useLanguage } from './Layout';
import { content, destinations } from '../lib/data';
import { X, ChevronLeft, ChevronRight } from 'lucide-react';

const Gallery: React.FC = () => {
    const { language, dir } = useLanguage();
    const t = content[language].gallery;
    const [selectedImage, setSelectedImage] = useState<number | null>(null);

    // Using destination images for gallery as well since we generated them
    const galleryImages = destinations.map(d => ({ src: d.image, alt: d.name[language] }));

    const openLightbox = (index: number) => setSelectedImage(index);
    const closeLightbox = () => setSelectedImage(null);

    const nextImage = (e: React.MouseEvent) => {
        e.stopPropagation();
        if (selectedImage !== null) {
            setSelectedImage((selectedImage + 1) % galleryImages.length);
        }
    };

    const prevImage = (e: React.MouseEvent) => {
        e.stopPropagation();
        if (selectedImage !== null) {
            setSelectedImage((selectedImage - 1 + galleryImages.length) % galleryImages.length);
        }
    };

    return (
        <section id="gallery" className="py-20 bg-white">
            <div className="container mx-auto px-4">
                <motion.div
                    initial={{ opacity: 0, y: 20 }}
                    whileInView={{ opacity: 1, y: 0 }}
                    viewport={{ once: true }}
                    className="text-center mb-12"
                >
                    <h2 className="text-3xl md:text-5xl font-bold text-secondary mb-4">{t.title}</h2>
                    <p className="text-neutral-dark/70 text-lg">{t.subtitle}</p>
                </motion.div>

                <div className="grid grid-cols-2 md:grid-cols-3 gap-4">
                    {galleryImages.map((img, index) => (
                        <motion.div
                            key={index}
                            initial={{ opacity: 0, scale: 0.9 }}
                            whileInView={{ opacity: 1, scale: 1 }}
                            viewport={{ once: true }}
                            transition={{ duration: 0.4, delay: index * 0.05 }}
                            className="relative aspect-square overflow-hidden rounded-xl cursor-pointer group"
                            onClick={() => openLightbox(index)}
                        >
                            <img
                                src={img.src}
                                alt={img.alt}
                                className="w-full h-full object-cover transition-transform duration-500 group-hover:scale-110"
                            />
                            <div className="absolute inset-0 bg-black/20 opacity-0 group-hover:opacity-100 transition-opacity duration-300 flex items-center justify-center">
                                <span className="text-white font-bold text-lg bg-black/50 px-4 py-2 rounded-full backdrop-blur-sm">
                                    View
                                </span>
                            </div>
                        </motion.div>
                    ))}
                </div>
            </div>

            {/* Lightbox */}
            <AnimatePresence>
                {selectedImage !== null && (
                    <motion.div
                        initial={{ opacity: 0 }}
                        animate={{ opacity: 1 }}
                        exit={{ opacity: 0 }}
                        className="fixed inset-0 z-50 bg-black/95 flex items-center justify-center p-4"
                        onClick={closeLightbox}
                    >
                        <button
                            onClick={closeLightbox}
                            className="absolute top-4 right-4 text-white hover:text-primary transition-colors p-2"
                        >
                            <X size={32} />
                        </button>

                        <button
                            onClick={dir === 'rtl' ? nextImage : prevImage}
                            className="absolute left-4 text-white hover:text-primary transition-colors p-2 hidden md:block"
                        >
                            <ChevronLeft size={40} />
                        </button>

                        <div className="relative max-w-4xl max-h-[80vh] w-full" onClick={e => e.stopPropagation()}>
                            <motion.img
                                key={selectedImage}
                                initial={{ opacity: 0, x: 20 }}
                                animate={{ opacity: 1, x: 0 }}
                                src={galleryImages[selectedImage].src}
                                alt={galleryImages[selectedImage].alt}
                                className="w-full h-full object-contain rounded-lg shadow-2xl"
                            />
                            <p className="text-white text-center mt-4 text-xl font-medium">
                                {galleryImages[selectedImage].alt}
                            </p>
                        </div>

                        <button
                            onClick={dir === 'rtl' ? prevImage : nextImage}
                            className="absolute right-4 text-white hover:text-primary transition-colors p-2 hidden md:block"
                        >
                            <ChevronRight size={40} />
                        </button>
                    </motion.div>
                )}
            </AnimatePresence>
        </section>
    );
};

export default Gallery;
