import React, { useState } from 'react';
import { motion } from 'framer-motion';
import OptimizedImage from './OptimizedImage';
import { ArrowRight } from 'lucide-react';
import { Link } from 'react-router-dom';
import { useLanguage } from './Layout';

interface FlipCardProps {
    frontImage: string;
    frontAlt: string;
    backTitle: string;
    backDescription: string;
    backCta: string;
    backLink: string;
}

const FlipCard: React.FC<FlipCardProps> = ({
    frontImage,
    frontAlt,
    backTitle,
    backDescription,
    backCta,
    backLink
}) => {
    const [isFlipped, setIsFlipped] = useState(false);
    const { dir } = useLanguage();

    const handleFlip = () => {
        setIsFlipped(!isFlipped);
    };

    return (
        <div
            className="relative w-full h-[400px] cursor-pointer perspective-1000 group"
            onMouseEnter={() => setIsFlipped(true)}
            onMouseLeave={() => setIsFlipped(false)}
            onClick={handleFlip}
        >
            <motion.div
                className="w-full h-full relative preserve-3d transition-all duration-500"
                animate={{ rotateY: isFlipped ? 180 : 0 }}
                transition={{ duration: 0.6, ease: "easeInOut" }}
                style={{ transformStyle: 'preserve-3d' }}
            >
                {/* Front Face */}
                <div
                    className="absolute inset-0 w-full h-full backface-hidden rounded-2xl overflow-hidden shadow-2xl"
                    style={{ backfaceVisibility: 'hidden' }}
                >
                    <OptimizedImage
                        src={frontImage}
                        alt={frontAlt}
                        className="w-full h-full"
                    />
                    <div className="absolute inset-0 bg-gradient-to-t from-black/60 via-transparent to-transparent opacity-60" />
                    <div className="absolute bottom-6 left-6 right-6 text-white">
                        <p className="text-sm font-bold uppercase tracking-wider mb-2 opacity-80">Click / Hover</p>
                        <h3 className="text-2xl font-bold">{frontAlt}</h3>
                    </div>
                </div>

                {/* Back Face */}
                <div
                    className="absolute inset-0 w-full h-full backface-hidden rounded-2xl overflow-hidden shadow-2xl bg-primary text-white p-8 flex flex-col justify-center items-center text-center rotate-y-180"
                    style={{ backfaceVisibility: 'hidden', transform: 'rotateY(180deg)' }}
                >
                    <div className="absolute inset-0 bg-[url('/public/images/pattern.png')] opacity-10" /> {/* Optional pattern */}

                    <h3 className="text-2xl font-bold mb-4 relative z-10">{backTitle}</h3>
                    <p className="text-white/90 mb-8 leading-relaxed relative z-10">
                        {backDescription}
                    </p>

                    <Link
                        to={backLink}
                        className="inline-flex items-center gap-2 bg-white text-primary px-6 py-3 rounded-full font-bold hover:bg-neutral-100 transition-colors relative z-10"
                        onClick={(e) => e.stopPropagation()} // Prevent flipping back immediately if clicking link
                    >
                        {backCta}
                        <ArrowRight size={18} className={dir === 'rtl' ? 'rotate-180' : ''} />
                    </Link>
                </div>
            </motion.div>
        </div>
    );
};

export default FlipCard;
