import React from 'react';
import { motion } from 'framer-motion';
import { useLanguage } from './Layout';
import { content, destinations } from '../lib/data';
import { ArrowRight, MapPin, BookOpen } from 'lucide-react';

const Destinations: React.FC = () => {
    const { language, dir } = useLanguage();
    const t = content[language].destinations;

    return (
        <section id="destinations" className="py-20 bg-neutral-light overflow-hidden">
            <div className="container mx-auto px-4">
                <motion.div
                    initial={{ opacity: 0, y: 20 }}
                    whileInView={{ opacity: 1, y: 0 }}
                    viewport={{ once: true }}
                    transition={{ duration: 0.6 }}
                    className="text-center mb-12"
                >
                    <h2 className="text-3xl md:text-5xl font-bold text-primary mb-4">{t.title}</h2>
                    <p className="text-neutral-dark/70 text-lg max-w-2xl mx-auto">{t.subtitle}</p>
                </motion.div>

                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
                    {destinations.map((dest, index) => (
                        <motion.div
                            key={dest.id}
                            initial={{ opacity: 0, y: 30 }}
                            whileInView={{ opacity: 1, y: 0 }}
                            viewport={{ once: true }}
                            transition={{ duration: 0.5, delay: index * 0.1 }}
                            className="group relative h-[450px] rounded-2xl overflow-hidden shadow-lg cursor-pointer bg-white flex flex-col"
                        >
                            {/* Image Section */}
                            <div className="relative h-2/3 overflow-hidden">
                                <img
                                    src={dest.image}
                                    alt={dest.name[language]}
                                    className="w-full h-full object-cover transition-transform duration-700 group-hover:scale-110"
                                />
                                <div className="absolute inset-0 bg-gradient-to-t from-black/60 to-transparent opacity-80" />
                                <div className="absolute bottom-4 left-4 right-4 text-white">
                                    <div className="flex items-center gap-2 mb-1 text-accent">
                                        <MapPin size={16} />
                                        <span className="text-xs font-medium uppercase tracking-wider">Yemen</span>
                                    </div>
                                    <h3 className="text-2xl font-bold">{dest.name[language]}</h3>
                                </div>
                            </div>

                            {/* Content Section */}
                            <div className="p-6 flex flex-col justify-between flex-1 relative z-10 bg-white">
                                <p className="text-neutral-dark/80 text-sm line-clamp-2 mb-4">
                                    {dest.description[language]}
                                </p>

                                {/* Blog/Story Preview */}
                                <div className="mt-auto pt-4 border-t border-neutral-100">
                                    <div className="flex items-center gap-2 text-primary text-sm font-bold mb-2">
                                        <BookOpen size={16} />
                                        <span>{language === 'ar' ? 'قصص من المكان' : 'Stories from here'}</span>
                                    </div>
                                    <p className="text-xs text-neutral-dark/60 line-clamp-1">
                                        {language === 'ar'
                                            ? 'اكتشف الأساطير والتاريخ العريق لهذه المنطقة...'
                                            : 'Discover the legends and ancient history of this region...'}
                                    </p>
                                </div>

                                <button className="absolute top-[-20px] right-6 bg-primary text-white p-3 rounded-full shadow-lg opacity-0 group-hover:opacity-100 transition-all duration-300 transform translate-y-4 group-hover:translate-y-0">
                                    <ArrowRight size={20} className={dir === 'rtl' ? 'rotate-180' : ''} />
                                </button>
                            </div>
                        </motion.div>
                    ))}
                </div>
            </div>
        </section>
    );
};

export default Destinations;
