import React from 'react';
import { motion } from 'framer-motion';
import { useLanguage } from './Layout';
import { content } from '../lib/data';
import { Link } from 'react-router-dom';
import { ArrowRight, Star, Users, MapPin } from 'lucide-react';
import OptimizedImage from './OptimizedImage';

const AboutTeaser: React.FC = () => {
    const { language, dir } = useLanguage();
    const t = content[language].aboutTeaser;

    const stats = [
        { icon: Star, value: '4.9', label: language === 'ar' ? 'تقييم الزوار' : 'Guest Rating' },
        { icon: Users, value: '5k+', label: language === 'ar' ? 'سائح سعيد' : 'Happy Tourists' },
        { icon: MapPin, value: '20+', label: language === 'ar' ? 'وجهة سياحية' : 'Destinations' },
    ];

    return (
        <section id="about-teaser" className="py-20 bg-neutral-light relative overflow-hidden">
            <div className="container mx-auto px-4">
                <div className="grid grid-cols-1 md:grid-cols-12 gap-6 auto-rows-[200px]">

                    {/* 1. Main Text Block (Large) */}
                    <motion.div
                        initial={{ opacity: 0, y: 20 }}
                        whileInView={{ opacity: 1, y: 0 }}
                        viewport={{ once: true }}
                        className="md:col-span-8 md:row-span-2 bg-white rounded-3xl p-8 md:p-12 flex flex-col justify-center shadow-sm hover:shadow-md transition-shadow"
                    >
                        <span className="text-primary font-bold tracking-wider uppercase text-sm mb-4 block">
                            {language === 'ar' ? 'من نحن' : 'Who We Are'}
                        </span>
                        <h2 className="text-3xl md:text-5xl font-bold text-neutral-dark mb-6 leading-tight">
                            {t.title}
                        </h2>
                        <p className="text-neutral-dark/70 text-lg leading-relaxed mb-8 max-w-2xl">
                            {t.description}
                        </p>
                        <Link
                            to="/about"
                            className="inline-flex items-center gap-2 text-primary font-bold text-lg hover:gap-4 transition-all group w-fit"
                        >
                            {t.cta}
                            <ArrowRight className={`transition-transform ${dir === 'rtl' ? 'rotate-180 group-hover:-translate-x-1' : 'group-hover:translate-x-1'}`} />
                        </Link>
                    </motion.div>

                    {/* 2. Visual Block (Image) */}
                    <motion.div
                        initial={{ opacity: 0, scale: 0.95 }}
                        whileInView={{ opacity: 1, scale: 1 }}
                        viewport={{ once: true }}
                        transition={{ delay: 0.1 }}
                        className="md:col-span-4 md:row-span-2 rounded-3xl overflow-hidden relative group"
                    >
                        <OptimizedImage
                            src="/images/shibam_hadramout_1764603231216.webp"
                            alt="Yemen Architecture"
                            className="w-full h-full object-cover transition-transform duration-700 group-hover:scale-110"
                        />
                        <div className="absolute inset-0 bg-gradient-to-t from-black/60 to-transparent" />
                        <div className="absolute bottom-6 left-6 right-6 text-white">
                            <p className="font-bold text-lg">{language === 'ar' ? 'اكتشف الأصالة' : 'Discover Authenticity'}</p>
                        </div>
                    </motion.div>

                    {/* 3. Stats Blocks (Small) */}
                    {stats.map((stat, index) => (
                        <motion.div
                            key={index}
                            initial={{ opacity: 0, y: 20 }}
                            whileInView={{ opacity: 1, y: 0 }}
                            viewport={{ once: true }}
                            transition={{ delay: 0.2 + (index * 0.1) }}
                            className="md:col-span-4 bg-white rounded-3xl p-6 flex flex-col justify-center items-center text-center shadow-sm hover:shadow-md transition-shadow"
                        >
                            <stat.icon className="w-8 h-8 text-primary mb-3" />
                            <h4 className="text-3xl font-bold text-neutral-dark mb-1">{stat.value}</h4>
                            <p className="text-neutral-dark/60 text-sm">{stat.label}</p>
                        </motion.div>
                    ))}

                </div>
            </div>
        </section>
    );
};

export default AboutTeaser;
