import React from 'react';
import { motion } from 'framer-motion';
import { useLanguage } from '../components/Layout';
import { content } from '../lib/data';
import { Target, Eye, Heart, Award, Users, Globe } from 'lucide-react';
import SEO from '../components/SEO';
import HeroCarousel from '../components/HeroCarousel';

const About: React.FC = () => {
    const { language } = useLanguage();
    const t = content[language].aboutFull;
    const nav = content[language].nav;

    const heroImages = [
        '/images/hero_background_1764603109548.webp',
        '/images/shibam_hadramout_1764603231216.webp',
        '/images/socotra_dragon_tree_1764603178352.webp'
    ];

    const values = [
        {
            icon: Award,
            title: { ar: 'الجودة', en: 'Quality' },
            description: { ar: 'نلتزم بتقديم أعلى مستويات الجودة في كل رحلة', en: 'We commit to delivering the highest quality in every journey' }
        },
        {
            icon: Users,
            title: { ar: 'الضيافة', en: 'Hospitality' },
            description: { ar: 'نرحب بكم بكرم الضيافة اليمنية الأصيلة', en: 'We welcome you with authentic Yemeni hospitality' }
        },
        {
            icon: Globe,
            title: { ar: 'الاستدامة', en: 'Sustainability' },
            description: { ar: 'نحافظ على تراثنا الطبيعي والثقافي للأجيال القادمة', en: 'We preserve our natural and cultural heritage for future generations' }
        }
    ];

    return (
        <div className="bg-neutral-light dark:bg-neutral-900 transition-colors duration-300 min-h-screen">
            <SEO
                title={language === 'ar' ? 'من نحن' : 'About Us'}
                description={language === 'ar' ? 'تعرف على قصتنا ورؤيتنا.' : 'Learn about our story and vision.'}
            />

            {/* Hero Carousel */}
            <HeroCarousel
                images={heroImages}
                title={nav.about}
                subtitle={language === 'ar' ? 'اكتشف قصتنا ورؤيتنا' : 'Discover our story and vision'}
            />

            <div className="container mx-auto px-4 py-20">
                {/* Mission Section */}
                <motion.div
                    initial={{ opacity: 0, y: 30 }}
                    whileInView={{ opacity: 1, y: 0 }}
                    viewport={{ once: true }}
                    className="max-w-4xl mx-auto mb-20"
                >
                    <div className="flex items-center justify-center gap-4 mb-8">
                        <Target size={40} className="text-primary" />
                        <h2 className="text-3xl md:text-5xl font-bold text-neutral-dark dark:text-white">
                            {t.mission.title}
                        </h2>
                    </div>
                    <p className="text-xl text-neutral-dark/80 dark:text-gray-300 leading-relaxed text-center">
                        {t.mission.description}
                    </p>
                </motion.div>

                {/* Vision Section */}
                <motion.div
                    initial={{ opacity: 0, y: 30 }}
                    whileInView={{ opacity: 1, y: 0 }}
                    viewport={{ once: true }}
                    className="max-w-4xl mx-auto mb-20"
                >
                    <div className="flex items-center justify-center gap-4 mb-8">
                        <Eye size={40} className="text-secondary" />
                        <h2 className="text-3xl md:text-5xl font-bold text-neutral-dark dark:text-white">
                            {t.vision.title}
                        </h2>
                    </div>
                    <p className="text-xl text-neutral-dark/80 dark:text-gray-300 leading-relaxed text-center">
                        {t.vision.description}
                    </p>
                </motion.div>

                {/* Values Section */}
                <motion.div
                    initial={{ opacity: 0, y: 30 }}
                    whileInView={{ opacity: 1, y: 0 }}
                    viewport={{ once: true }}
                    className="mb-20"
                >
                    <div className="flex items-center justify-center gap-4 mb-12">
                        <Heart size={40} className="text-accent" />
                        <h2 className="text-3xl md:text-5xl font-bold text-neutral-dark dark:text-white">
                            {t.values.title}
                        </h2>
                    </div>
                    <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
                        {values.map((value, index) => (
                            <motion.div
                                key={index}
                                initial={{ opacity: 0, y: 30 }}
                                whileInView={{ opacity: 1, y: 0 }}
                                viewport={{ once: true }}
                                transition={{ delay: index * 0.1 }}
                                className="bg-white dark:bg-neutral-800 p-8 rounded-2xl shadow-lg hover:shadow-xl transition-all hover:-translate-y-2 border border-transparent dark:border-neutral-700"
                            >
                                <div className="bg-primary/10 w-16 h-16 rounded-full flex items-center justify-center mb-6 mx-auto">
                                    <value.icon size={32} className="text-primary" />
                                </div>
                                <h3 className="text-xl font-bold text-neutral-dark dark:text-white mb-4 text-center">
                                    {value.title[language]}
                                </h3>
                                <p className="text-neutral-dark/70 dark:text-gray-300 text-center leading-relaxed">
                                    {value.description[language]}
                                </p>
                            </motion.div>
                        ))}
                    </div>
                </motion.div>
            </div>
        </div>
    );
};

export default About;
