import React from 'react';
import { motion } from 'framer-motion';
import { useLanguage } from './Layout';
import { content, destinations } from '../lib/data';
import { ArrowRight, MapPin } from 'lucide-react';

const Destinations: React.FC = () => {
    const { language, dir } = useLanguage();
    const t = content[language].destinations;

    return (
        <section id="destinations" className="py-20 bg-neutral-light overflow-hidden">
            <div className="container mx-auto px-4">
                <motion.div
                    initial={{ opacity: 0, y: 20 }}
                    whileInView={{ opacity: 1, y: 0 }}
                    viewport={{ once: true }}
                    transition={{ duration: 0.6 }}
                    className="text-center mb-12"
                >
                    <h2 className="text-3xl md:text-5xl font-bold text-primary mb-4">{t.title}</h2>
                    <p className="text-neutral-dark/70 text-lg max-w-2xl mx-auto">{t.subtitle}</p>
                </motion.div>

                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
                    {destinations.map((dest, index) => (
                        <motion.div
                            key={dest.id}
                            initial={{ opacity: 0, y: 30 }}
                            whileInView={{ opacity: 1, y: 0 }}
                            viewport={{ once: true }}
                            transition={{ duration: 0.5, delay: index * 0.1 }}
                            className="group relative h-[400px] rounded-2xl overflow-hidden shadow-lg cursor-pointer"
                        >
                            <img
                                src={dest.image}
                                alt={dest.name[language]}
                                className="w-full h-full object-cover transition-transform duration-700 group-hover:scale-110"
                            />

                            {/* Gradient Overlay */}
                            <div className="absolute inset-0 bg-gradient-to-t from-black/80 via-black/20 to-transparent opacity-90 transition-opacity duration-300" />

                            {/* Content */}
                            <div className="absolute bottom-0 left-0 right-0 p-6 text-white transform transition-transform duration-300 translate-y-2 group-hover:translate-y-0">
                                <div className="flex items-center gap-2 mb-2 text-accent">
                                    <MapPin size={18} />
                                    <span className="text-sm font-medium uppercase tracking-wider">Yemen</span>
                                </div>
                                <h3 className="text-2xl font-bold mb-2">{dest.name[language]}</h3>
                                <p className="text-white/80 text-sm mb-4 line-clamp-2 group-hover:line-clamp-none transition-all">
                                    {dest.description[language]}
                                </p>

                                <button className="flex items-center gap-2 text-primary font-bold opacity-0 group-hover:opacity-100 transition-opacity duration-300 transform translate-y-4 group-hover:translate-y-0">
                                    {t.discover}
                                    <ArrowRight size={18} className={dir === 'rtl' ? 'rotate-180' : ''} />
                                </button>
                            </div>
                        </motion.div>
                    ))}
                </div>
            </div>
        </section>
    );
};

export default Destinations;
