import React, { useRef } from 'react';
import { motion, useScroll, useTransform } from 'framer-motion';
import OptimizedImage from './OptimizedImage';
import { ArrowRight } from 'lucide-react';
import { Link } from 'react-router-dom';
import { useLanguage } from './Layout';

interface ParallaxCardProps {
    image: string;
    alt: string;
    title: string;
    description: string;
    cta: string;
    link: string;
}

const ParallaxCard: React.FC<ParallaxCardProps> = ({
    image,
    alt,
    title,
    description,
    cta,
    link
}) => {
    const { dir } = useLanguage();
    const ref = useRef<HTMLDivElement>(null);
    const { scrollYProgress } = useScroll({
        target: ref,
        offset: ["start end", "end start"]
    });

    const y = useTransform(scrollYProgress, [0, 1], ["-10%", "10%"]);

    return (
        <div ref={ref} className="relative w-full h-[500px] md:h-[600px] rounded-3xl overflow-hidden shadow-2xl group">
            {/* Parallax Background Image */}
            <motion.div
                style={{ y }}
                className="absolute inset-0 w-full h-[120%]"
            >
                <OptimizedImage
                    src={image}
                    alt={alt}
                    className="w-full h-full object-cover"
                />
                <div className="absolute inset-0 bg-black/20" />
            </motion.div>

            {/* Floating Glass Card */}
            <div className="absolute bottom-0 left-0 right-0 p-6 md:p-8">
                <div className="bg-white/10 backdrop-blur-md border border-white/20 rounded-2xl p-6 md:p-8 shadow-lg text-white transform transition-transform duration-300 hover:-translate-y-2">
                    <h3 className="text-2xl md:text-3xl font-bold mb-4">{title}</h3>
                    <p className="text-white/90 mb-6 leading-relaxed line-clamp-3 md:line-clamp-none">
                        {description}
                    </p>

                    <Link
                        to={link}
                        className="inline-flex items-center gap-2 bg-white text-primary px-6 py-3 rounded-full font-bold hover:bg-neutral-100 transition-colors"
                    >
                        {cta}
                        <ArrowRight size={18} className={dir === 'rtl' ? 'rotate-180' : ''} />
                    </Link>
                </div>
            </div>
        </div>
    );
};

export default ParallaxCard;
