import React from 'react';
import { motion } from 'framer-motion';
import { useLanguage } from '../components/Layout';
import { content, destinations } from '../lib/data';
import { MapPin, BookOpen } from 'lucide-react';
import SEO from '../components/SEO';

const DestinationsPage: React.FC = () => {
    const { language, dir } = useLanguage();
    const t = content[language].destinations;

    return (
        <div className="bg-neutral-light min-h-screen">
            <SEO
                title={language === 'ar' ? 'الوجهات' : 'Destinations'}
                description={language === 'ar' ? 'اكتشف وجهات اليمن الساحرة وتاريخها العريق.' : 'Discover Yemen\'s enchanting destinations and ancient history.'}
            />

            {/* Hero Banner for Navbar Visibility */}
            <div className="relative h-[40vh] min-h-[300px] flex items-center justify-center overflow-hidden">
                <img
                    src="/images/socotra_dragon_tree_1764603178352.png"
                    alt="Destinations Hero"
                    className="absolute inset-0 w-full h-full object-cover"
                />
                <div className="absolute inset-0 bg-black/50" />
                <motion.div
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    className="relative z-10 text-center px-4"
                >
                    <h1 className="text-4xl md:text-6xl font-bold text-white mb-4">{t.title}</h1>
                    <p className="text-white/90 text-lg max-w-2xl mx-auto">{t.subtitle}</p>
                </motion.div>
            </div>

            <div className="container mx-auto px-4 py-20">
                <div className="space-y-24">
                    {destinations.map((dest, index) => (
                        <motion.div
                            key={dest.id}
                            initial={{ opacity: 0, y: 50 }}
                            whileInView={{ opacity: 1, y: 0 }}
                            viewport={{ once: true, margin: "-100px" }}
                            transition={{ duration: 0.8 }}
                            className={`flex flex-col ${index % 2 === 0 ? 'lg:flex-row' : 'lg:flex-row-reverse'} gap-8 lg:gap-16 items-center`}
                        >
                            {/* Image Side */}
                            <div className="w-full lg:w-1/2">
                                <div className="relative rounded-3xl overflow-hidden shadow-2xl aspect-[4/3] group">
                                    <img
                                        src={dest.image}
                                        alt={dest.name[language]}
                                        loading="lazy"
                                        className="w-full h-full object-cover transition-transform duration-700 group-hover:scale-105"
                                    />
                                    <div className="absolute inset-0 bg-gradient-to-t from-black/50 to-transparent opacity-60" />
                                    <div className="absolute bottom-6 left-6 right-6 text-white">
                                        <div className="flex items-center gap-2 mb-2 text-accent font-bold tracking-wider uppercase">
                                            <MapPin size={18} />
                                            <span>Yemen</span>
                                        </div>
                                    </div>
                                </div>
                            </div>

                            {/* Content Side */}
                            <div className="w-full lg:w-1/2">
                                <h2 className="text-3xl md:text-5xl font-bold text-neutral-dark mb-6">
                                    {dest.name[language]}
                                </h2>
                                <p className="text-xl text-neutral-dark/80 leading-relaxed mb-8">
                                    {dest.description[language]}
                                </p>

                                <div className="bg-white p-8 rounded-2xl shadow-lg border-l-4 border-primary relative overflow-hidden hover:shadow-xl transition-shadow">
                                    <div className="absolute top-0 right-0 p-4 opacity-10">
                                        <BookOpen size={100} className="text-primary" />
                                    </div>
                                    <h3 className="text-xl font-bold text-primary mb-4 flex items-center gap-2">
                                        <BookOpen size={24} />
                                        {language === 'ar' ? 'قصة من التاريخ' : 'History & Stories'}
                                    </h3>
                                    <p className="text-neutral-dark/70 leading-relaxed relative z-10">
                                        {dest.story ? dest.story[language] : (language === 'ar' ? 'قصة هذا المكان قيد الكتابة...' : 'The story of this place is being written...')}
                                    </p>
                                </div>
                            </div>
                        </motion.div>
                    ))}
                </div>
            </div>
        </div>
    );
};

export default DestinationsPage;
