import React, { useRef, useState } from 'react';
import { motion } from 'framer-motion';
import { useLanguage } from '../components/Layout';
import { content } from '../lib/data';
import { Mail, Phone, MapPin, Send, CheckCircle, AlertCircle } from 'lucide-react';
import { FaWhatsapp } from 'react-icons/fa';
import WhatsAppIcon from '../components/WhatsAppIcon';
import SEO from '../components/SEO';
import emailjs from '@emailjs/browser';
import confetti from 'canvas-confetti';

const ContactPage: React.FC = () => {
    const { language, dir } = useLanguage();
    const t = content[language].contact;
    const form = useRef<HTMLFormElement>(null);
    const [status, setStatus] = useState<'idle' | 'sending' | 'success' | 'error'>('idle');

    const triggerConfetti = () => {
        const duration = 3000;
        const animationEnd = Date.now() + duration;
        const defaults = { startVelocity: 30, spread: 360, ticks: 60, zIndex: 0 };

        const randomInRange = (min: number, max: number) => Math.random() * (max - min) + min;

        const interval: any = setInterval(function () {
            const timeLeft = animationEnd - Date.now();

            if (timeLeft <= 0) {
                return clearInterval(interval);
            }

            const particleCount = 50 * (timeLeft / duration);
            confetti({ ...defaults, particleCount, origin: { x: randomInRange(0.1, 0.3), y: Math.random() - 0.2 } });
            confetti({ ...defaults, particleCount, origin: { x: randomInRange(0.7, 0.9), y: Math.random() - 0.2 } });
        }, 250);
    };

    const sendEmail = (e: React.FormEvent) => {
        e.preventDefault();
        if (!form.current) return;

        setStatus('sending');

        // Replace these with your actual EmailJS credentials
        emailjs.sendForm('service_placeholder', 'template_placeholder', form.current, 'public_key_placeholder')
            .then((result) => {
                console.log(result.text);
                setStatus('success');
                triggerConfetti();
                form.current?.reset();
            }, (error) => {
                console.log(error.text);
                setStatus('error');
            });
    };

    return (
        <div className="bg-neutral-light dark:bg-neutral-900 transition-colors duration-300 min-h-screen">
            <SEO
                title={language === 'ar' ? 'تواصل معنا' : 'Contact Us'}
                description={language === 'ar' ? 'تواصل مع فريق جواهر اليمن لتخطيط رحلتك.' : 'Contact Yemen Gems team to plan your trip.'}
            />

            {/* Hero Banner for Navbar Visibility */}
            <div className="relative h-[40vh] min-h-[300px] flex items-center justify-center overflow-hidden">
                <img
                    src="/images/almukalla_harbor_1764603566692.png"
                    alt="Contact Hero"
                    className="absolute inset-0 w-full h-full object-cover"
                />
                <div className="absolute inset-0 bg-black/60" />
                <motion.div
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    className="relative z-10 text-center px-4"
                >
                    <h1 className="text-4xl md:text-6xl font-bold text-white mb-4">{t.title}</h1>
                    <p className="text-white/90 text-lg max-w-2xl mx-auto">{t.subtitle}</p>
                </motion.div>
            </div>

            <div className="container mx-auto px-4 py-20">
                <div className="grid grid-cols-1 lg:grid-cols-2 gap-12 max-w-6xl mx-auto">
                    {/* Contact Info Cards */}
                    <motion.div
                        initial={{ opacity: 0, x: dir === 'rtl' ? 50 : -50 }}
                        animate={{ opacity: 1, x: 0 }}
                        transition={{ delay: 0.2 }}
                        className="space-y-6"
                    >
                        {/* Address Card */}
                        <a
                            href={t.info.googleMaps}
                            target="_blank"
                            rel="noopener noreferrer"
                            className="block group"
                        >
                            <div className="bg-white dark:bg-neutral-800 p-8 rounded-2xl shadow-lg flex items-start gap-6 transition-all duration-300 group-hover:-translate-y-2 group-hover:shadow-xl border border-transparent group-hover:border-primary/20 dark:border-neutral-700">
                                <div className="bg-primary/10 p-4 rounded-full text-primary group-hover:bg-primary group-hover:text-white transition-colors">
                                    <MapPin size={32} />
                                </div>
                                <div>
                                    <h3 className="text-xl font-bold text-neutral-dark dark:text-white mb-2 group-hover:text-primary transition-colors">
                                        {language === 'ar' ? 'العنوان' : 'Address'}
                                    </h3>
                                    <p className="text-neutral-dark/70 dark:text-gray-300 text-lg">{t.info.address}</p>
                                    <span className="text-sm text-primary mt-2 inline-block opacity-0 group-hover:opacity-100 transition-opacity">
                                        {language === 'ar' ? 'عرض على الخريطة' : 'View on Map'} →
                                    </span>
                                </div>
                            </div>
                        </a>

                        {/* Phone Card */}
                        <a
                            href={`https://wa.me/${t.info.phone.replace(/\s+/g, '')}`}
                            target="_blank"
                            rel="noopener noreferrer"
                            className="block group"
                        >
                            <div className="bg-white dark:bg-neutral-800 p-8 rounded-2xl shadow-lg flex items-start gap-6 transition-all duration-300 group-hover:-translate-y-2 group-hover:shadow-xl border border-transparent group-hover:border-secondary/20 dark:border-neutral-700">
                                <div className="bg-secondary/10 p-4 rounded-full text-secondary group-hover:bg-secondary group-hover:text-white transition-colors">
                                    <WhatsAppIcon size={32} />
                                </div>
                                <div>
                                    <h3 className="text-xl font-bold text-neutral-dark dark:text-white mb-2 group-hover:text-secondary transition-colors">
                                        {language === 'ar' ? 'واتساب' : 'WhatsApp'}
                                    </h3>
                                    <p className="text-neutral-dark/70 dark:text-gray-300 text-lg" dir="ltr">{t.info.phone}</p>
                                    <span className="text-sm text-secondary mt-2 inline-block opacity-0 group-hover:opacity-100 transition-opacity">
                                        {language === 'ar' ? 'مراسلة فورية' : 'Chat Now'} →
                                    </span>
                                </div>
                            </div>
                        </a>

                        {/* Email Card */}
                        <a
                            href={`mailto:${t.info.email}`}
                            className="block group"
                        >
                            <div className="bg-white dark:bg-neutral-800 p-8 rounded-2xl shadow-lg flex items-start gap-6 transition-all duration-300 group-hover:-translate-y-2 group-hover:shadow-xl border border-transparent group-hover:border-accent/20 dark:border-neutral-700">
                                <div className="bg-accent/10 p-4 rounded-full text-accent group-hover:bg-accent group-hover:text-white transition-colors">
                                    <Mail size={32} />
                                </div>
                                <div>
                                    <h3 className="text-xl font-bold text-neutral-dark dark:text-white mb-2 group-hover:text-accent transition-colors">
                                        {language === 'ar' ? 'البريد الإلكتروني' : 'Email'}
                                    </h3>
                                    <p className="text-neutral-dark/70 dark:text-gray-300 text-lg">{t.info.email}</p>
                                </div>
                            </div>
                        </a>
                    </motion.div>

                    {/* Contact Form */}
                    <motion.div
                        initial={{ opacity: 0, x: dir === 'rtl' ? -50 : 50 }}
                        animate={{ opacity: 1, x: 0 }}
                        transition={{ delay: 0.4 }}
                        className="bg-white dark:bg-neutral-800 p-8 md:p-12 rounded-3xl shadow-2xl relative overflow-hidden"
                    >
                        <div className="absolute top-0 left-0 w-full h-2 bg-gradient-to-r from-primary via-secondary to-accent" />

                        <form ref={form} onSubmit={sendEmail} className="space-y-6 relative z-10">
                            <div>
                                <label className="block text-neutral-dark dark:text-white font-bold mb-2">{t.form.name}</label>
                                <input
                                    type="text"
                                    name="user_name"
                                    required
                                    className="w-full px-4 py-3 rounded-xl border border-neutral-200 dark:border-neutral-700 focus:border-primary focus:ring-2 focus:ring-primary/20 outline-none transition-all bg-neutral-50 dark:bg-neutral-900 dark:text-white hover:bg-white dark:hover:bg-neutral-900"
                                    placeholder={language === 'ar' ? 'الاسم' : 'Name'}
                                />
                            </div>

                            <div>
                                <label className="block text-neutral-dark dark:text-white font-bold mb-2">{t.form.email}</label>
                                <input
                                    type="email"
                                    name="user_email"
                                    required
                                    className="w-full px-4 py-3 rounded-xl border border-neutral-200 dark:border-neutral-700 focus:border-primary focus:ring-2 focus:ring-primary/20 outline-none transition-all bg-neutral-50 dark:bg-neutral-900 dark:text-white hover:bg-white dark:hover:bg-neutral-900"
                                    placeholder="name@example.com"
                                />
                            </div>

                            <div>
                                <label className="block text-neutral-dark dark:text-white font-bold mb-2">{t.form.message}</label>
                                <textarea
                                    name="message"
                                    required
                                    rows={4}
                                    className="w-full px-4 py-3 rounded-xl border border-neutral-200 dark:border-neutral-700 focus:border-primary focus:ring-2 focus:ring-primary/20 outline-none transition-all bg-neutral-50 dark:bg-neutral-900 dark:text-white hover:bg-white dark:hover:bg-neutral-900 resize-none"
                                    placeholder={language === 'ar' ? 'اكتب رسالتك هنا...' : 'Write your message here...'}
                                />
                            </div>

                            <button
                                type="submit"
                                disabled={status === 'sending' || status === 'success'}
                                className={`w-full font-bold py-4 rounded-xl shadow-lg transition-all flex items-center justify-center gap-2 group ${status === 'success' ? 'bg-green-500 text-white' :
                                        status === 'error' ? 'bg-red-500 text-white' :
                                            'bg-primary hover:bg-primary/90 text-white hover:shadow-xl hover:-translate-y-1'
                                    }`}
                            >
                                {status === 'sending' ? (
                                    <span className="animate-pulse">...</span>
                                ) : status === 'success' ? (
                                    <>
                                        <CheckCircle size={20} />
                                        {language === 'ar' ? 'تم الإرسال بنجاح' : 'Sent Successfully'}
                                    </>
                                ) : status === 'error' ? (
                                    <>
                                        <AlertCircle size={20} />
                                        {language === 'ar' ? 'فشل الإرسال' : 'Failed to Send'}
                                    </>
                                ) : (
                                    <>
                                        {t.form.submit}
                                        <Send size={20} className={`transition-transform ${dir === 'rtl' ? 'rotate-180 group-hover:-translate-x-1' : 'group-hover:translate-x-1'}`} />
                                    </>
                                )}
                            </button>
                        </form>
                    </motion.div>
                </div>
            </div>
        </div>
    );
};

export default ContactPage;
