import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { useLanguage } from './Layout';
import { content } from '../lib/data';
import { ChevronDown, ChevronLeft, ChevronRight } from 'lucide-react';

const Hero: React.FC = () => {
    const { language, dir } = useLanguage();
    const heroSlides = content[language].hero;
    const [currentIndex, setCurrentIndex] = useState(0);

    useEffect(() => {
        const timer = setInterval(() => {
            setCurrentIndex((prev) => (prev + 1) % heroSlides.length);
        }, 6000); // Increased duration for better readability
        return () => clearInterval(timer);
    }, [heroSlides.length]);

    const nextSlide = () => {
        setCurrentIndex((prev) => (prev + 1) % heroSlides.length);
    };

    const prevSlide = () => {
        setCurrentIndex((prev) => (prev - 1 + heroSlides.length) % heroSlides.length);
    };

    return (
        <section id="hero" className="relative h-screen w-full overflow-hidden flex items-center justify-center bg-black">
            {/* Background Carousel with Ken Burns Effect */}
            <AnimatePresence mode='wait'>
                <motion.div
                    key={currentIndex}
                    initial={{ opacity: 0, scale: 1.1 }}
                    animate={{ opacity: 1, scale: 1 }}
                    exit={{ opacity: 0 }}
                    transition={{ duration: 2, ease: "easeOut" }} // Slower, smoother transition
                    className="absolute inset-0 z-0"
                >
                    <img
                        src={heroSlides[currentIndex].image}
                        alt={heroSlides[currentIndex].title}
                        className="w-full h-full object-cover"
                    />
                    <div className="absolute inset-0 bg-black/30 bg-gradient-to-b from-black/70 via-transparent to-black/70" />
                </motion.div>
            </AnimatePresence>

            {/* Navigation Arrows */}
            <button
                onClick={dir === 'rtl' ? nextSlide : prevSlide}
                className="absolute left-4 z-20 text-white/50 hover:text-white transition-colors p-2 hidden md:block hover:bg-white/10 rounded-full"
            >
                <ChevronLeft size={48} />
            </button>
            <button
                onClick={dir === 'rtl' ? prevSlide : nextSlide}
                className="absolute right-4 z-20 text-white/50 hover:text-white transition-colors p-2 hidden md:block hover:bg-white/10 rounded-full"
            >
                <ChevronRight size={48} />
            </button>

            {/* Dots Navigation */}
            <div className="absolute bottom-24 left-1/2 -translate-x-1/2 z-20 flex gap-3">
                {heroSlides.map((_, index) => (
                    <button
                        key={index}
                        onClick={() => setCurrentIndex(index)}
                        className={`h-2 rounded-full transition-all duration-500 ${index === currentIndex ? 'bg-primary w-8' : 'bg-white/50 w-2 hover:bg-white'
                            }`}
                        aria-label={`Go to slide ${index + 1}`}
                    />
                ))}
            </div>

            {/* Content */}
            <div className="relative z-10 container mx-auto px-4 text-center text-white">
                <AnimatePresence mode='wait'>
                    <motion.div
                        key={currentIndex}
                        initial={{ opacity: 0, y: 30, filter: 'blur(10px)' }}
                        animate={{ opacity: 1, y: 0, filter: 'blur(0px)' }}
                        exit={{ opacity: 0, y: -30, filter: 'blur(10px)' }}
                        transition={{ duration: 0.8, ease: "easeOut" }}
                        className="max-w-4xl mx-auto"
                    >
                        <h1 className="text-5xl md:text-7xl lg:text-8xl font-bold mb-6 leading-tight text-shadow tracking-tight">
                            {heroSlides[currentIndex].title}
                        </h1>

                        <p className="text-xl md:text-2xl mb-10 text-white/90 font-light leading-relaxed">
                            {heroSlides[currentIndex].subtitle}
                        </p>

                        <div className="flex flex-col sm:flex-row gap-4 justify-center items-center">
                            <a
                                href="#destinations"
                                className="w-full sm:w-auto px-8 py-4 bg-primary hover:bg-primary/90 text-white rounded-full font-bold text-lg transition-all hover:scale-105 shadow-lg shadow-primary/30"
                            >
                                {heroSlides[currentIndex].cta_primary}
                            </a>
                            <a
                                href="#gallery"
                                className="w-full sm:w-auto px-8 py-4 bg-white/10 hover:bg-white/20 backdrop-blur-md border border-white/30 text-white rounded-full font-bold text-lg transition-all hover:scale-105"
                            >
                                {heroSlides[currentIndex].cta_secondary}
                            </a>
                        </div>
                    </motion.div>
                </AnimatePresence>
            </div>

            {/* Scroll Indicator */}
            <motion.div
                animate={{ y: [0, 10, 0] }}
                transition={{ duration: 2, repeat: Infinity }}
                className="absolute bottom-8 left-1/2 -translate-x-1/2 text-white/50 hover:text-white transition-colors cursor-pointer"
                onClick={() => document.getElementById('about-teaser')?.scrollIntoView({ behavior: 'smooth' })}
            >
                <ChevronDown size={32} />
            </motion.div>
        </section>
    );
};

export default Hero;
