import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { useLanguage } from './Layout';
import { content } from '../lib/data';
import { ChevronDown, ChevronLeft, ChevronRight } from 'lucide-react';

const heroImages = [
    {
        src: '/images/hero_background_1764603109548.png',
        alt: 'Sana\'a Old City',
    },
    {
        src: '/images/socotra_dragon_tree_1764603178352.png',
        alt: 'Socotra Island',
    },
    {
        src: '/images/shibam_hadramout_1764603231216.png',
        alt: 'Shibam Hadramout',
    }
];

const Hero: React.FC = () => {
    const { language, dir } = useLanguage();
    const t = content[language].hero;
    const [currentIndex, setCurrentIndex] = useState(0);

    useEffect(() => {
        const timer = setInterval(() => {
            setCurrentIndex((prev) => (prev + 1) % heroImages.length);
        }, 5000);
        return () => clearInterval(timer);
    }, []);

    const nextSlide = () => {
        setCurrentIndex((prev) => (prev + 1) % heroImages.length);
    };

    const prevSlide = () => {
        setCurrentIndex((prev) => (prev - 1 + heroImages.length) % heroImages.length);
    };

    return (
        <section id="hero" className="relative h-screen w-full overflow-hidden flex items-center justify-center">
            {/* Background Carousel */}
            <AnimatePresence mode='wait'>
                <motion.div
                    key={currentIndex}
                    initial={{ opacity: 0, scale: 1.1 }}
                    animate={{ opacity: 1, scale: 1 }}
                    exit={{ opacity: 0 }}
                    transition={{ duration: 1.5 }}
                    className="absolute inset-0 z-0"
                >
                    <img
                        src={heroImages[currentIndex].src}
                        alt={heroImages[currentIndex].alt}
                        className="w-full h-full object-cover"
                    />
                    <div className="absolute inset-0 bg-black/40 bg-gradient-to-b from-black/60 via-transparent to-black/60" />
                </motion.div>
            </AnimatePresence>

            {/* Navigation Arrows */}
            <button
                onClick={dir === 'rtl' ? nextSlide : prevSlide}
                className="absolute left-4 z-20 text-white/50 hover:text-white transition-colors p-2 hidden md:block"
            >
                <ChevronLeft size={48} />
            </button>
            <button
                onClick={dir === 'rtl' ? prevSlide : nextSlide}
                className="absolute right-4 z-20 text-white/50 hover:text-white transition-colors p-2 hidden md:block"
            >
                <ChevronRight size={48} />
            </button>

            {/* Dots Navigation */}
            <div className="absolute bottom-24 left-1/2 -translate-x-1/2 z-20 flex gap-3">
                {heroImages.map((_, index) => (
                    <button
                        key={index}
                        onClick={() => setCurrentIndex(index)}
                        className={`w-3 h-3 rounded-full transition-all ${index === currentIndex ? 'bg-primary w-8' : 'bg-white/50 hover:bg-white'
                            }`}
                    />
                ))}
            </div>

            {/* Content */}
            <div className="relative z-10 container mx-auto px-4 text-center text-white">
                <AnimatePresence mode='wait'>
                    <motion.div
                        key={currentIndex} // Re-animate text on slide change
                        initial={{ opacity: 0, y: 20 }}
                        animate={{ opacity: 1, y: 0 }}
                        exit={{ opacity: 0, y: -20 }}
                        transition={{ duration: 0.8 }}
                    >
                        <h1 className="text-4xl md:text-6xl lg:text-7xl font-bold mb-6 leading-tight text-shadow">
                            {t.title}
                        </h1>

                        <p className="text-lg md:text-2xl mb-10 max-w-2xl mx-auto text-white/90 font-light">
                            {t.subtitle}
                        </p>
                    </motion.div>
                </AnimatePresence>

                <motion.div
                    initial={{ opacity: 0, y: 30 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.8, delay: 0.6 }}
                    className="flex flex-col sm:flex-row gap-4 justify-center items-center"
                >
                    <a
                        href="#destinations"
                        className="w-full sm:w-auto px-8 py-4 bg-primary hover:bg-primary/90 text-white rounded-full font-bold text-lg transition-transform hover:scale-105 shadow-lg shadow-primary/30"
                    >
                        {t.cta_primary}
                    </a>
                    <a
                        href="#gallery"
                        className="w-full sm:w-auto px-8 py-4 bg-white/10 hover:bg-white/20 backdrop-blur-sm border border-white/30 text-white rounded-full font-bold text-lg transition-all"
                    >
                        {t.cta_secondary}
                    </a>
                </motion.div>
            </div>

            {/* Scroll Indicator */}
            <motion.div
                animate={{ y: [0, 10, 0] }}
                transition={{ duration: 2, repeat: Infinity }}
                className="absolute bottom-10 left-1/2 -translate-x-1/2 text-white/70"
            >
                <ChevronDown size={32} />
            </motion.div>
        </section>
    );
};

export default Hero;
