import React, { useState, useEffect, useRef } from 'react';
import Slider from 'react-slick';
import { motion } from 'framer-motion';
import { useLanguage } from '../components/Layout';
import { destinations } from '../lib/data';
import { ChevronLeft, ChevronRight } from 'lucide-react';

// Custom Arrow Components
const NextArrow = (props: any) => {
    const { onClick } = props;
    return (
        <button
            onClick={onClick}
            className="absolute right-4 top-1/2 -translate-y-1/2 z-10 bg-black/50 hover:bg-primary text-white p-3 rounded-full transition-all"
            aria-label="Next Slide"
        >
            <ChevronRight size={24} />
        </button>
    );
};

const PrevArrow = (props: any) => {
    const { onClick } = props;
    return (
        <button
            onClick={onClick}
            className="absolute left-4 top-1/2 -translate-y-1/2 z-10 bg-black/50 hover:bg-primary text-white p-3 rounded-full transition-all"
            aria-label="Previous Slide"
        >
            <ChevronLeft size={24} />
        </button>
    );
};

const GalleryPage: React.FC = () => {
    const { language, dir } = useLanguage();
    const [nav1, setNav1] = useState<Slider | null>(null);
    const [nav2, setNav2] = useState<Slider | null>(null);
    const slider1 = useRef<Slider>(null);
    const slider2 = useRef<Slider>(null);

    useEffect(() => {
        setNav1(slider1.current);
        setNav2(slider2.current);
    }, []);

    // Use destination images + duplicate to have enough for demo
    const images = [...destinations, ...destinations].map((d, i) => ({
        id: i,
        src: d.image,
        alt: d.name[language],
        caption: d.description[language]
    }));

    const mainSettings = {
        dots: false,
        infinite: true,
        speed: 500,
        slidesToShow: 1,
        slidesToScroll: 1,
        autoplay: true,
        autoplaySpeed: 5000,
        pauseOnHover: true,
        lazyLoad: 'ondemand' as const,
        fade: true,
        nextArrow: <NextArrow />,
        prevArrow: <PrevArrow />,
        rtl: dir === 'rtl',
    };

    const thumbSettings = {
        dots: false,
        infinite: true,
        speed: 500,
        slidesToShow: 4,
        slidesToScroll: 1,
        swipeToSlide: true,
        focusOnSelect: true,
        centerMode: true,
        responsive: [
            {
                breakpoint: 768,
                settings: {
                    slidesToShow: 3,
                }
            }
        ],
        rtl: dir === 'rtl',
    };

    return (
        <div className="pt-24 pb-20 bg-neutral-dark min-h-screen text-white">
            <div className="container mx-auto px-4">
                <motion.div
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    className="text-center mb-12"
                >
                    <h1 className="text-4xl md:text-6xl font-bold text-primary mb-4">
                        {language === 'ar' ? 'معرض الصور' : 'Photo Gallery'}
                    </h1>
                    <p className="text-white/70 text-lg">
                        {language === 'ar' ? 'استكشف جمال اليمن بعدسة فنية' : 'Explore the beauty of Yemen through an artistic lens'}
                    </p>
                </motion.div>

                <div className="max-w-5xl mx-auto">
                    {/* Main Slider */}
                    <div className="mb-8 rounded-2xl overflow-hidden shadow-2xl border border-white/10 relative bg-black">
                        <Slider asNavFor={nav2 || undefined} ref={slider1} {...mainSettings}>
                            {images.map((img) => (
                                <div key={img.id} className="relative aspect-video outline-none">
                                    <img
                                        src={img.src}
                                        alt={img.alt}
                                        className="w-full h-full object-contain"
                                    />
                                    <div className="absolute bottom-0 left-0 right-0 bg-black/60 p-4 backdrop-blur-sm">
                                        <h3 className="text-xl font-bold text-primary mb-1">{img.alt}</h3>
                                        <p className="text-sm text-white/80">{img.caption}</p>
                                    </div>
                                </div>
                            ))}
                        </Slider>
                    </div>

                    {/* Thumbnail Slider */}
                    <div className="px-8">
                        <Slider
                            asNavFor={nav1 || undefined}
                            ref={slider2}
                            {...thumbSettings}
                            className="gallery-thumbs"
                        >
                            {images.map((img) => (
                                <div key={img.id} className="px-2 cursor-pointer outline-none">
                                    <div className="rounded-lg overflow-hidden border-2 border-transparent hover:border-primary transition-all h-24 relative">
                                        <img
                                            src={img.src}
                                            alt={img.alt}
                                            className="w-full h-full object-cover"
                                        />
                                        <div className="absolute inset-0 bg-black/20 hover:bg-transparent transition-colors" />
                                    </div>
                                </div>
                            ))}
                        </Slider>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default GalleryPage;
