import React from 'react';
import { motion } from 'framer-motion';
import { useLanguage } from './Layout';
import { content } from '../lib/data';
import { Link } from 'react-router-dom';
import { ArrowRight } from 'lucide-react';
import FlipCard from './FlipCard';

const AboutTeaser: React.FC = () => {
    const { language, dir } = useLanguage();
    const t = content[language].aboutTeaser;

    return (
        <section id="about-teaser" className="py-20 bg-white relative overflow-hidden">
            {/* Decorative Background Element */}
            <div className="absolute top-0 right-0 w-64 h-64 bg-primary/5 rounded-full blur-3xl -translate-y-1/2 translate-x-1/2" />

            <div className="container mx-auto px-4">
                <div className="flex flex-col md:flex-row items-center gap-12">
                    {/* Text Content */}
                    <motion.div
                        initial={{ opacity: 0, x: dir === 'rtl' ? 50 : -50 }}
                        whileInView={{ opacity: 1, x: 0 }}
                        viewport={{ once: true }}
                        transition={{ duration: 0.8 }}
                        className="flex-1"
                    >
                        <span className="text-primary font-bold tracking-wider uppercase text-sm mb-2 block">
                            {language === 'ar' ? 'من نحن' : 'Who We Are'}
                        </span>
                        <h2 className="text-3xl md:text-5xl font-bold text-neutral-dark mb-6 leading-tight">
                            {t.title}
                        </h2>
                        <h3 className="text-xl text-secondary font-medium mb-6">
                            {t.subtitle}
                        </h3>
                        <p className="text-neutral-dark/70 text-lg leading-relaxed mb-8">
                            {t.description}
                        </p>

                        <Link
                            to="/about"
                            className="inline-flex items-center gap-2 text-primary font-bold text-lg hover:gap-4 transition-all group"
                        >
                            {t.cta}
                            <ArrowRight className={`transition-transform ${dir === 'rtl' ? 'rotate-180 group-hover:-translate-x-1' : 'group-hover:translate-x-1'}`} />
                        </Link>
                    </motion.div>

                    {/* Image/Visual Content */}
                    <motion.div
                        initial={{ opacity: 0, scale: 0.9 }}
                        whileInView={{ opacity: 1, scale: 1 }}
                        viewport={{ once: true }}
                        transition={{ duration: 0.8 }}
                        className="flex-1 relative"
                    >
                        <FlipCard
                            frontImage="/images/shibam_hadramout_1764603231216.webp"
                            frontAlt={language === 'ar' ? 'العمارة اليمنية' : 'Yemen Architecture'}
                            backTitle={language === 'ar' ? 'اكتشف المزيد' : 'Discover More'}
                            backDescription={language === 'ar' ? 'انغمس في تاريخ وثقافة اليمن الغنية. كل زاوية تحكي قصة.' : 'Immerse yourself in the rich history and culture of Yemen. Every corner tells a story.'}
                            backCta={t.cta}
                            backLink="/about"
                        />
                        {/* Decorative Frame */}
                        <div className="absolute inset-0 border-2 border-primary/20 rounded-2xl -rotate-2 scale-105 z-0 pointer-events-none" />
                    </motion.div>
                </div>
            </div>
        </section>
    );
};

export default AboutTeaser;
