import React from 'react';
import { motion } from 'framer-motion';
import { useLanguage } from '../components/Layout';
import { content } from '../lib/data';

const About: React.FC = () => {
    const { language } = useLanguage();
    // We'll need to add about content to data.ts later, using placeholders for now or generic text

    return (
        <div className="pt-24 pb-20 container mx-auto px-4 min-h-screen">
            <motion.div
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                className="max-w-4xl mx-auto"
            >
                <h1 className="text-4xl md:text-6xl font-bold text-primary mb-8 text-center">
                    {language === 'ar' ? 'عن جواهر اليمن' : 'About Yemen Gems'}
                </h1>

                <div className="bg-white p-8 rounded-2xl shadow-lg border border-neutral-200/50">
                    <p className="text-lg leading-relaxed mb-6 text-neutral-dark/80">
                        {language === 'ar'
                            ? 'جواهر اليمن هي بوابتك الأولى لاستكشاف جمال وتاريخ وحضارة اليمن السعيد. نحن نهدف إلى تسليط الضوء على الكنوز المخفية في هذا البلد العريق، من جزيرة سقطرى الساحرة إلى ناطحات السحاب الطينية في شبام.'
                            : 'Yemen Gems is your premier gateway to exploring the beauty, history, and civilization of Yemen. We aim to highlight the hidden treasures of this ancient land, from the enchanting Socotra Island to the mud-brick skyscrapers of Shibam.'}
                    </p>
                    <p className="text-lg leading-relaxed mb-6 text-neutral-dark/80">
                        {language === 'ar'
                            ? 'رؤيتنا هي تقديم صورة حقيقية ومشرقة عن اليمن للعالم، وتشجيع السياحة المستدامة التي تحترم الثقافة والبيئة المحلية.'
                            : 'Our vision is to present a true and bright image of Yemen to the world, and to encourage sustainable tourism that respects local culture and environment.'}
                    </p>
                </div>
            </motion.div>
        </div>
    );
};

export default About;
